export function agentIcon(name) {
    return AGENT_ICONS[name] || '';
}

export function agentWithIcon(name) {
    return agentIcon(name) + escapeHtml(name);
}

export function projectName(cwd) {
    if (!cwd) return '~';
    var parts = cwd.split('/').filter(Boolean);
    return parts[parts.length - 1] || '~';
}

export function wingDisplayName(wing) {
    if (!wing) return '';
    return wing.wing_label || wing.hostname || '';
}

export function shortenPath(path) {
    if (path.indexOf('/Users/') === 0) {
        var parts = path.split('/');
        return '~/' + parts.slice(3).join('/');
    }
    if (path.indexOf('/home/') === 0) {
        var parts = path.split('/');
        return '~/' + parts.slice(3).join('/');
    }
    return path;
}

export function formatRelativeTime(ms) {
    var diff = Date.now() - ms;
    if (diff < 60000) return 'just now';
    if (diff < 3600000) return Math.floor(diff / 60000) + 'm ago';
    if (diff < 86400000) return Math.floor(diff / 3600000) + 'h ago';
    return Math.floor(diff / 86400000) + 'd ago';
}

export function formatAuditTime(secs) {
    var m = Math.floor(secs / 60);
    var s = Math.floor(secs % 60);
    return m + ':' + (s < 10 ? '0' : '') + s;
}

export function semverCompare(a, b) {
    var pa = (a || '').match(/^v?(\d+)\.(\d+)\.(\d+)/);
    var pb = (b || '').match(/^v?(\d+)\.(\d+)\.(\d+)/);
    if (!pa || !pb) return 0;
    for (var i = 1; i <= 3; i++) {
        var diff = parseInt(pa[i]) - parseInt(pb[i]);
        if (diff !== 0) return diff;
    }
    return 0;
}

export function dirParent(value) {
    var last = value.lastIndexOf('/');
    if (last === -1) return { dir: value, prefix: '' };
    return { dir: value.substring(0, last + 1), prefix: value.substring(last + 1).toLowerCase() };
}

export function nestedRepoCount(dirPath, projects) {
    var prefix = dirPath + '/';
    var count = 0;
    for (var i = 0; i < projects.length; i++) {
        var p = projects[i].path;
        if (p === dirPath || p.indexOf(prefix) === 0) count++;
    }
    return count;
}

// Base64 helpers (used by crypto and tunnel modules)
export function b64ToBytes(b64) {
    return Uint8Array.from(atob(b64), function(c) { return c.charCodeAt(0); });
}

export function bytesToB64(bytes) {
    var binary = '';
    for (var i = 0; i < bytes.length; i++) binary += String.fromCharCode(bytes[i]);
    return btoa(binary);
}

export function b64urlToBytes(b64url) {
    var b64 = b64url.replace(/-/g, '+').replace(/_/g, '/');
    while (b64.length % 4) b64 += '=';
    return b64ToBytes(b64);
}

export function bytesToB64url(bytes) {
    return bytesToB64(bytes).replace(/\+/g, '-').replace(/\//g, '_').replace(/=+$/, '');
}
